from tkinter import Tk, Canvas, Entry, Text, Button, PhotoImage

from database import create_connection


OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / Path(r"./assets/Details")


def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

def open_details_page(previous_window, sname, amount, service, user_id, user_name):
    previous_window.destroy()
    window = Tk()

    window.geometry("700x840")
    window.configure(bg = "#ECECEE")

    def open_payment():
        # Get input values
        dd = entry_6.get()
        mm = entry_4.get()
        yyyy = entry_5.get()
        hh = entry_7.get()
        mi = entry_8.get()
        
        # Create new database connection
        conn = create_connection()
        if conn is not None:
            try:
                cursor = conn.cursor()
                # Update query with correct column names
                update_query = """
                    INSERT into history (uid, sname, service, amount, 
                        dd, mm, yyyy, hh, min) 
                    values (%s, %s, %s, %s, %s, %s, %s, %s, %s)
                """
                update_values = (user_id, sname, service, amount, dd, mm, yyyy, hh, mi)
                
                cursor.execute(update_query, update_values)
                conn.commit()
                
                from Payment.Payment import open_payment_page
                open_payment_page(window, user_id, user_name, amount)
                
            except Exception as e:
                print(f"Database error: {e}")
                conn.rollback()
            finally:
                cursor.close()
                conn.close()
        else:
            print("Failed to connect to database")

